window.DEBUG_CONFIG={enabled:false,modules:{main:false,products:false,cart:false,auth:false,wishlist:false,collections:false,shop:false},levels:{error:true,warn:true,info:false,debug:false}};window.debugLog={log:(module,message,...args)=>{if(window.DEBUG_CONFIG.enabled&&(window.DEBUG_CONFIG.modules[module]||window.DEBUG_CONFIG.levels.debug)){console.log(`[${module.toUpperCase()}]${message}`,...args);}},info:(module,message,...args)=>{if(window.DEBUG_CONFIG.enabled&&(window.DEBUG_CONFIG.modules[module]||window.DEBUG_CONFIG.levels.info)){console.info(`[${module.toUpperCase()}]${message}`,...args);}},warn:(module,message,...args)=>{if(window.DEBUG_CONFIG.levels.warn){console.warn(`[${module.toUpperCase()}]${message}`,...args);}},error:(module,message,...args)=>{if(window.DEBUG_CONFIG.levels.error){console.error(`[${module.toUpperCase()}]${message}`,...args);}}};window.toggleDebug=(enabled=null)=>{if(enabled===null){window.DEBUG_CONFIG.enabled=!window.DEBUG_CONFIG.enabled;}else{window.DEBUG_CONFIG.enabled=enabled;}console.log(`Debug mode:${window.DEBUG_CONFIG.enabled?'ENABLED':'DISABLED'}`);};window.enableModuleDebug=(module)=>{window.DEBUG_CONFIG.modules[module]=true;console.log(`Debug enabled for module:${module}`);};